import React, {Component} from 'react'
import Button from 'react-bootstrap/Button';
import "./App.css";
class List extends Component{
    constructor(props){
      super(props)
      this.state = {
        list:["puta","wowo","i figured out how react works","nice"],
        task:""
      }
      this.changeText = this.changeText.bind(this)
    };
    changeText(event) {
      this.setState({
        name: event.target.value
      });
    }
     onClearList =  () => {
        this.setState({
          list: []
        });
     }
      addTask = () =>{
      this.setState({
        list: [...this.state.list, this.state.name]
    });
  }
    render (){
    return( 
    <div>
        <ol>{this.todos = this.state.list.map((post) =>
      <li> {post} </li>
      )}</ol>
      <form>
        <input type="text" id="task" onChange={this.changeText}></input>
      </form>
      <Button variant="primary" type="submit" onClick={this.addTask}> Add Task </Button>
      <Button variant="primary" type="submit" onClick={this.onClearList}> Clear List</Button>
        </div>

    );
  }
}



export default List;
