import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import Web3 from 'web3';
import TruffleContract from "/truffle-contract";
import Todo from "/build/Todo.json";



const web3 = new Web3(Web3.givenProvider || "http://localhost:8545");
web3.eth.getAccounts().then(console.log);

console.log(web3.eth.getAccounts())
console.log(web3.eth.accounts[0])



class List extends Component {
  componentWillMount() {
    this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545")
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    //var todo = web3.eth.contract(PASTE ABI HERE!);
//const todo = web3.eth.Contract(Todo.abi,networkData.address)



    const todo = TruffleContract(Todo);
    todo.setProvider(web3.currentProvider);
    console.log(todo)

  }
  constructor(props) {
    super(props);
    this.state = {
      list: ["puta", "wowo", "i figured out how react works", "nice"],
      task: "",
      account: ''
    };
    this.changeText = this.changeText.bind(this);


  }


  changeText(event) {
    this.setState({
      name: event.target.value
    });
  }
  onClearList = () => {
    this.setState({
      list: []
    });
  };
  addTask = () => {
    this.setState({
      list: [...this.state.list, this.state.name]
    });

  };

  render() {
    return (
      <div className="main">
        <Card className="cards">
          <Card.Body className="cardbodies">
            <h1>Things to do:{this.state.account}</h1>
            <ol>
              {(this.todos = this.state.list.map(post => <li> {post} </li>))}
            </ol>
            <form>
              <input type="text" id="task" onChange={this.changeText} />
            </form>
          </Card.Body>
        </Card>
        <Button
          className="buttontime"
          variant="primary"
          type="submit"
          onClick={this.addTask}
        >
          {" "}
          Add Task{" "}
        </Button>
        <Button
          className="buttontime"
          variant="primary"
          type="submit"
          onClick={this.onClearList}
        >
          {" "}
          Clear List
        </Button>
      </div>
    );
  }
}

export default List;
