import React, {Component} from 'react'

class List extends Component{
    constructor(props){
      super(props)
      this.state = {
        list:["puta","wowo","i figured out how react works","nice"]
      }

      function addToDo() {
          this.state.list.push("wow here is a todo")
      }





      this.todos = this.state.list.map((post) =>
      <li> {post} </li>
      )
    }


    render (){ 
        addToDo()
    return <div>

        <ul> {this.todos} </ul>

        </div>
}
};


export default List;
