import React, {Component} from 'react'

class List extends Component{
    constructor(props){
      super(props)
      this.state = {
        list:["puta","wowo","i figured out how react works","nice"],
        task:""
      }
      this.changeText = this.changeText.bind(this)
    };
    changeText(event) {
      this.setState({
        name: event.target.value
      });
    }

     onClearArray =  () => {

      this.state.list = []
      };

  addTodo = () =>{
      this.setState({
        list: [...this.state.list, this.state.name]
    });
  }

    render (){

    return( 
    <div>

        <ul>     {this.todos = this.state.list.map((post) =>
      <li> {post} </li>
      )}</ul>


      <form>
        <input type="text" id="task" onChange={this.changeText}></input>
      </form>
      <button onClick={this.addTodo}> Click me </button>
      <button onClick={this.onClearArray}> Delete Me</button>
        </div>

    );
  }
}



export default List;
