class List extends Component{
    constructor(props){
      super(props)
      this.state = {
        list:["puta","wowo","i figured out how react works","nice"]
      }

      function addToDo() {
          this.state.list.push("wow here is a todo")
      }
      onClearArray = () => {
        this.setState({ list: [] });
      };
      this.setState({
        list: [...this.state.list, 'new value']
    });

      this.todos = this.state.list.map((post) =>
      <li> {post} </li>
      )
    }

    render (){ 

    return <div>

        <ul> {this.todos} </ul>
      <button onClick={this.setState}> Click me </button>
      <button onClick={this.onClearArray}> Delete Me</button>
        </div>
}
};


export default List;
