class List extends Component{
    constructor(props){
      super(props)
      this.state = {
        list:["puta","wowo","i figured out how react works","nice"]
      }


     let  onClearArray = async () => {
        this.setState({ list: [] });
      };

      let addTodo = () =>{
      this.setState({
        list: [...this.state.list, 'new value']
    });
  }

      this.todos = this.state.list.map((post) =>
      <li> {post} </li>
      );

    };



    render (){

    return( 
    <div>

        <ul> {this.todos} </ul>
      <button onClick={this.addTodo}> Click me </button>
      <button onClick={this.onClearArray}> Delete Me</button>
        </div>

    );
  }
}



export default List;
