import React, {Component} from 'react'

class List extends Component{
    constructor(props){
      super(props)
      this.state = {
        list:["puta","wowo","i figured out how react works","nice"]
      }

      function addToDo() {
          this.state.list.push("wow here is a todo")
      }

     let onAddItem = () => {
      var newArray = this.state.list.slice();    
      newArray.push("new value");   
      this.setState({list:newArray})
      };


      this.todos = this.state.list.map((post) =>
      <li> {post} </li>
      )
    }

    render (){ 

    return <div>

        <ul> {this.todos} </ul>
      <button onClick={this.onAddItem}> Click me </button>
        </div>
}
};


export default List;
